from rest_framework import serializers
from ..models import Client, ClientSetting

class ClientListSerializer(serializers.ModelSerializer):
    class Meta:
        model = Client
        fields = '__all__'

class ClientRetrieveSerializer(serializers.ModelSerializer):
    class Meta:
        model = Client
        fields = '__all__'

class ClientWriteSerializer(serializers.ModelSerializer):
    class Meta:
        model = Client
        fields = '__all__'

class ClientSettingListSerializer(serializers.ModelSerializer):
    clients = ClientListSerializer(many=True, read_only=True)

    class Meta:
        model = ClientSetting
        fields = '__all__'

class ClientSettingRetrieveSerializer(serializers.ModelSerializer):
    clients = ClientRetrieveSerializer(many=True, read_only=True)

    class Meta:
        model = ClientSetting
        fields = '__all__'

class ClientSettingWriteSerializer(serializers.ModelSerializer):
    client_ids = serializers.ListField(
        child=serializers.IntegerField(), write_only=True
    )

    class Meta:
        model = ClientSetting
        fields = ['client_ids', 'section', 'loop_type', 'delay_time']

    def create(self, validated_data):
        client_ids = validated_data.pop('client_ids', [])
        client_setting = ClientSetting.objects.create(**validated_data)

        # Add multiple clients to the ManyToManyField
        clients = Client.objects.filter(id__in=client_ids)
        client_setting.client.set(clients)

        return client_setting

    def update(self, instance, validated_data):
        client_ids = validated_data.pop('client_ids', [])
        instance.section = validated_data.get('section', instance.section)
        instance.loop_type = validated_data.get('loop_type', instance.loop_type)
        instance.delay_time = validated_data.get('delay_time', instance.delay_time)
        instance.save()

        # Update clients in the ManyToManyField
        if client_ids:
            clients = Client.objects.filter(id__in=client_ids)
            instance.client.set(clients)

        return instance
